mapper.select:
SELECT a.feed_id, contents, location, a.created_at, a.writer_user_id, uid AS writerUid , nick_name AS writerNickName, pic AS writerPic , fl.feed_id IS NOT NULL AS isLike , COUNT(flk.feed_id) AS likeCount FROM feed a JOIN user b ON a.writer_user_id = b.user_id LEFT JOIN feed_like fl ON a.feed_id = fl.feed_id AND fl.user_id = #{signedUserId} LEFT JOIN feed_like flk ON a.feed_id = flk.feed_id
WHERE a.writer_user_id = #{profileUserId}
GROUP BY a.feed_id ORDER BY a.feed_id DESC LIMIT #{startIdx}, #{size}

SELECT pic
FROM feed_pic
WHERE feed_id = #{feedId}
